function ShowMonitorScreenMonitorWindowGlobals  % check for an undefined window or a broken window and (re)open it in either caseA = isempty(MonitorWindow);if ~A, r = screen(MonitorWindow,'rect'); A = isempty(r); end;if A,	screens = screen('screens');	if ~any(MonitorWindowMonitor==screens),		error(['Available screens are ' ...			mat2str(screen) ' but MonitorWindowMonitor is ' ...			int2str(MonitorWindowMonitor) '.']);	end;	MonitorWindow = screen(MonitorWindowMonitor,'OpenWindow',128);	screen(MonitorWindow,'PixelSize',8,1);	MonitorWindowDepth = 8;	MonitorWindowRect = screen(MonitorWindow,'Rect');	MonitorWindowRefresh = screen(MonitorWindow,'FrameRate',[]);end;