function [frameCounter, clut_entries] = MakeClutFrameCounter(entries, numFrames) % entries = [ bg c], c ranges from 1..256, not 0..255FrameCounterGlobals;N = ceil(numFrames/FrameCounterDivs) + 1;clut_entries = repmat(fix(255 * [ 1 : -1/(FrameCounterDivs-1) : 0]),3,N)';clut_entries = clut_entries(2:numFrames+1,:);frameCounter = struct('type', 'frameCounter', 'offscreen', [], ...                      'currentFrame', 1, 'bg', entries(1), ...					  'clut_index', entries(2)-1);