function [frameCounter] = MakeImageFrameCounter(colors)  % colors are entries of a clutFrameCounterGlobals;width  = FrameCounterRect(1,3)-FrameCounterRect(1,1);height = FrameCounterRect(1,4)-FrameCounterRect(1,2);if (length(colors)~=FrameCounterDivs+1), error('Number of colors does not equal number of divisions.'); end;BG = colors(1);offscreen = zeros(1,length(colors)-1);for i=2:length(colors),     offscreen(i-1) = screen(-1,'OpenOffscreenWindow',255,[0 0 width height]);	 screen(offscreen(i-1),'PutImage',repmat(colors(i),height,width), ...	            [0 0 width height]);end;		frameCounter = struct('type', 'frameCounter', ...                      'offscreen', offscreen, 'curentFrame', 1, 'bg', colors(1), ...					  'clut_index', []);